var link=document.querySelector(".contacts-button"),popup=document.querySelector(".modal-contact-form"),close=popup.querySelector(".modal-close"),form=popup.querySelector(".contact-form"),userName=popup.querySelector("[name=name]"),mail=popup.querySelector("[name=mail]"),textarea=popup.querySelector("[name=message]");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),userName.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show"),popup.classList.remove("modal-error")}),form.addEventListener("submit",function(e){userName.value&&mail.value&&textarea.value||(e.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("modal-show")&&(popup.classList.remove("modal-show"),popup.classList.remove("modal-error"))});